import React, { useState } from "react";

export const Toggle = (props) => {
  const [isChecked, setIsChecked] = useState(props.isChecked); // Initialize isChecked with the prop

  const toggleForm = (value) => {
    setIsChecked(value);

    // Use isChecked to determine which form to switch to
    const formToSwitchTo = isChecked ? "register" : "login";
    props.onFormSwitch(formToSwitchTo);
  };

  const switchToRegister = () => {
    props.onFormSwitch(isChecked ? "login" : "register");
    setIsChecked(!isChecked); // Toggle the isChecked state
  };

  return (
    <div className="container">
      <div className="switches-container">
        <input
          type="radio"
          id="switchOne"
          name="switchToggle"
          value="false"
          checked={!isChecked}
          onChange={() => toggleForm(false)}
        />
        <input
          type="radio"
          id="switchTwo"
          name="switchToggle"
          value="true"
          checked={isChecked}
          onChange={() => toggleForm(true)}
        />
        <label htmlFor="switchOne">{isChecked ? "Log In" : "Returning User"}</label>
        <label htmlFor="switchTwo">Register</label>
        <div className="switch-wrapper">
          <div className="switch" onClick={switchToRegister}>
            <div>{isChecked ? "Back" : "Returning User"}</div>
            <div>Register</div>
          </div>
        </div>
      </div>
    </div>
  );
};
