import React, { useState } from "react";

export const Toggle = (props) => {
    const [isChecked, setIsChecked] = useState(false);
    const [currentform, setCurrent] = useState(props.currentForm);
    console.log(currentform);

    const toggleForm = (value) => {
        setIsChecked(value); // Set the isChecked state

        // Use isChecked to determine which form to switch to
        const formToSwitchTo = value === false ? "login" : "register";
        props.onFormSwitch(formToSwitchTo);
    };

    const switchToRegister = () => {
        props.onFormSwitch(isChecked === false ? "register" : " ");
        setIsChecked(true); // Set isChecked to true
    };

    return (
        <div className="container">
            <div className="switches-container">
                <input
                    type="radio"
                    id="switchOne"
                    name="switchToggle"
                    value="false"
                    checked={isChecked === false}
                    onChange={() => toggleForm(false)}
                />
                <input
                    type="radio"
                    id="switchTwo"
                    name="switchToggle"
                    value="true"
                    checked={isChecked === true}
                    onChange={() => toggleForm(true)}
                />
                <label htmlFor="switchOne">{isChecked ? "Log In" : "Returning User"}</label>
                <label htmlFor="switchTwo">Register</label>
                <div className="switch-wrapper">
                    <div className="switch" onClick={switchToRegister}>
                        <div>{isChecked ? "Back" : "Returning User"}</div>
                        <div>Register</div>
                    </div>
                </div>
            </div>
        </div>
    );
};
